ValidationRule.errorConditionFormula:
AND(OR(CONTAINS($Profile.Name, 'SSSW'),$Profile.Name = 'Unified Customer Service'),
		OR(ISNEW(),OR(ISCHANGED(PersonMailingStreet),ISCHANGED(PersonMailingState),ISCHANGED(PersonMailingCity),ISCHANGED(PersonMailingPostalCode),ISCHANGED(PersonMailingCountry),ISCHANGED(MailingStreet2__c))),

OR(AND(OR(NOT(ISBLANK(PersonMailingStreet)),NOT(ISBLANK(PersonMailingState)),NOT(ISBLANK(PersonMailingCity))),OR(NOT(ISNUMBER( PersonMailingPostalCode )),LEN( PersonMailingPostalCode ) <> 4,ISBLANK(PersonMailingPostalCode)),OR(PersonMailingCountry = 'AU',ISBLANK(PersonMailingCountry)) ), 
AND(PersonMailingCountry = 'AU', NOT(ISBLANK(PersonMailingPostalCode)),
OR(NOT(ISNUMBER( PersonMailingPostalCode )),LEN( PersonMailingPostalCode ) <> 4) 

),AND(NOT(ISNUMBER( PersonMailingPostalCode )) ,NOT(ISBLANK( PersonMailingPostalCode )),PersonMailingCountry = 'AU' )
)

)
