{!REQUIRESCRIPT("/soap/ajax/54.0/apex.js")} 
{!REQUIRESCRIPT("/support/console/31.0/integration.js")} 

var urlStr = '';
if ('{!Article__c.RecordType}' == 'Consignment') {
urlStr = '/apex/StarTrackViewAllScanEvents?id={!Article__c.Id}';
} else {
urlStr = '/apex/StarTrackViewAllScanEvents?id={!Article__c.ConsignmentId__c}';
}

var openSubtab = function(result){ 
sforce.console.openSubtab(result.id, urlStr, true, 'Scan Events', null); 
}; 
if (sforce.console.isInConsole()) {
sforce.console.getEnclosingPrimaryTabId(openSubtab);
} else {
window.open(urlStr, '_self');
}
