CustomField.formula:
If (ISNULL(ClosedDate), "N/A",
    if( 
        ( 24 * 
            (
                ( 5 * FLOOR( ( DATEVALUE( ClosedDate ) - DATE( 1900, 1, 8) ) / 7) 
                    + MIN(5, 
                        MOD(DATEVALUE( ClosedDate ) - DATE( 1900, 1, 8), 7) 
                        + MIN( 1, 24 / 24 * 
                            ( MOD( ClosedDate - DATETIMEVALUE( '1900-01-08 16:00:00' ), 1 ) 
                            ) 
                        ) 
                    )
                )
                -
                ( 5 * FLOOR( ( DATEVALUE( CreatedDate ) - DATE( 1900, 1, 8) ) / 7) 
                    + MIN( 5, 
                        MOD( DATEVALUE( CreatedDate ) - DATE( 1996, 1, 1), 7 )
                        + MIN( 1, 24 / 24 * 
                            ( MOD( CreatedDate - DATETIMEVALUE( '1900-01-08 16:00:00' ), 1) 
                            ) 
                        )
                    )
                )
             )
        )
    < 24 , "SDR", "NSDR" )
)
