ValidationRule.errorConditionFormula:
AND(OR(CONTAINS($Profile.Name, 'SSSW'),$Profile.Name = 'CustomerService'),OR(ISNEW(),OR(ISCHANGED(MailingStreet),ISCHANGED(MailingState),ISCHANGED(MailingCity),ISCHANGED(MailingPostalCode),ISCHANGED(MailingCountry),ISCHANGED(MailingStreet2__c))),

OR(AND(OR(NOT(ISBLANK(MailingStreet)),NOT(ISBLANK(MailingState)),NOT(ISBLANK(MailingCity))),OR(NOT(ISNUMBER( MailingPostalCode )),LEN( MailingPostalCode ) <> 4,ISBLANK(MailingPostalCode)),OR(MailingCountry = 'AU',ISBLANK(MailingCountry)) ), 
AND(MailingCountry = 'AU', NOT(ISBLANK(MailingPostalCode)),
OR(NOT(ISNUMBER( MailingPostalCode )),LEN( MailingPostalCode ) <> 4) 

),AND(NOT(ISNUMBER( MailingPostalCode )) ,NOT(ISBLANK( MailingPostalCode )),MailingCountry = 'AU' )
)

)
