CustomField.formula:
/* No Errors */
IF(Opp_Hygiene_Rating__c =IMAGE("/img/samples/flag_green.gif","Opportunity Data Good"),"Opportunity Hygiene Good",

/* No Product */
IF(Product_Count__c  < 1, "No Product Entered, ","") & "" &

/* No Amount */
IF(Amount  <= 0,  "Zero Amount Entered, ","") & "" &

/* No Type */
IF(ISPICKVAL(Type, ""),"No Type entered, ", "") & "" &

/* Not Closed but Closed Date in the Past */
IF(
OR(ISPICKVAL(StageName, "Noticed"),  ISPICKVAL(StageName, "Identified"), ISPICKVAL(StageName, "Validated"),  ISPICKVAL(StageName, "Qualified"),  ISPICKVAL(StageName, "Conditional Agreement")) && (CloseDate  <=    TODAY()),  "Closed Date today or in the past, ","") & "" &

/* Closed but Closed Date in the Future*/
 IF(
OR(ISPICKVAL(StageName, "Closed Won"),  ISPICKVAL(StageName, "Closed Lost"), ISPICKVAL(StageName, "Closed Disqualified")) && (CloseDate  > TODAY()),"Closed Date in the future, ",""))
