CustomPageWebLink.url:
{!REQUIRESCRIPT("/support/console/32.0/integration.js")} 

if('{!Case.RecordType}' === 'SSSW General Enquiry') { 

	sforce.console.getEnclosingPrimaryTabId(function(result) { 
		sforce.console.openSubtab(result.id, '/apex/FacilitySearch?postcode={!Case.Address2Postcode__c}&isdtp=vw', true); 
	}); 

} else { 

	sforce.console.getEnclosingPrimaryTabId(function(result) { 
		// Note: the below is a workaround due to a bug with Omni triggering new case whenever a subtab is opened with "id" in the url param
		var targetUrl = '/apex/FacilitySearch?id={!Case.Id}&postcode={!Case.Address2Postcode__c}&isdtp=vw';
		var omniRedirectURL = '/apex/OmniBugRedirect?&url=' + encodeURIComponent(targetUrl);
		sforce.console.openSubtab(result.id, omniRedirectURL, true); 
	}); 
}
